// components/auth/login/login.component.ts

import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {
  loginForm!: FormGroup;
  loginError: string = '';

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private authService: AuthService
  ) {}

  ngOnInit(): void {
    this.initForm();
  }

  initForm(): void {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });
  }

  onSubmit(): void {
    if (this.loginForm.invalid) {
      return;
    }

    const { email, password } = this.loginForm.value;

    if (this.authService.login(email, password)) {
      // Get the redirect URL from the auth service
      const redirectUrl = this.authService.redirectUrl || '/';

      // Reset the redirect URL
      this.authService.redirectUrl = null;

      // Navigate to the redirect URL
      this.router.navigate([redirectUrl]);
    } else {
      this.loginError = 'Invalid email or password';
    }
  }
}
<!-- components/auth/login/login.component.html -->

<div class="container">
  <div class="row">
    <div class="col-md-6 offset-md-3 col-lg-4 offset-lg-4">
      <div class="card mt-5">
        <div class="card-header bg-primary text-white">
          <h4 class="mb-0">Login</h4>
        </div>
        <div class="card-body">
          <form [formGroup]="loginForm" (ngSubmit)="onSubmit()">
            <div class="mb-3">
              <label for="email" class="form-label">Email address</label>
              <input 
                type="email" 
                class="form-control" 
                id="email"
                formControlName="email"
                placeholder="admin@example.com"
              >
              <div class="text-danger" *ngIf="loginForm.get('email')?.invalid && loginForm.get('email')?.touched">
                Please enter a valid email address
              </div>
            </div>

            <div class="mb-3">
              <label for="password" class="form-label">Password</label>
              <input 
                type="password" 
                class="form-control" 
                id="password"
                formControlName="password"
                placeholder="password"
              >
              <div class="text-danger" *ngIf="loginForm.get('password')?.invalid && loginForm.get('password')?.touched">
                Password is required
              </div>
            </div>

            <div class="alert alert-info" role="alert">
              <strong>Demo Credentials:</strong><br>
              Email: admin@example.com<br>
              Password: password
            </div>

            <div class="d-grid">
              <button 
                type="submit" 
                class="btn btn-primary"
                [disabled]="loginForm.invalid"
              >
                Login
              </button>
            </div>

            <div class="alert alert-danger mt-3" *ngIf="loginError">
              {{ loginError }}
            </div>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
