// components/products/product-detail/product-detail.component.ts

import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Product } from '../../../models/product.model';
import { ProductService } from '../../../services/product.service';
import { switchMap } from 'rxjs/operators';

@Component({
  selector: 'app-product-detail',
  templateUrl: './product-detail.component.html',
  styleUrls: ['./product-detail.component.css']
})
export class ProductDetailComponent implements OnInit {
  product: Product = {} as Product;
  prevProductId: string | null = null;
  nextProductId: string | null = null;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private productService: ProductService
  ) {}

  ngOnInit(): void {
    // Method 1: Using router resolver (preferred for pre-loading data)
    this.route.data.subscribe(data => {
      this.product = data['product'];
      this.loadAdjacentProducts();
    });

    // Method 2: Using route params directly (alternative approach)
    /*
    this.route.paramMap.pipe(
      switchMap(params => {
        const id = params.get('id');
        return id ? this.productService.getProduct(id) : of({} as Product);
      })
    ).subscribe(product => {
      this.product = product;
      this.loadAdjacentProducts();
    });
    */
  }

  loadAdjacentProducts(): void {
    this.productService.getProducts().subscribe(products => {
      if (!this.product.id) return;

      const currentIndex = products.findIndex(p => p.id === this.product.id);
      if (currentIndex === -1) return;

      // Find previous and next product IDs
      this.prevProductId = currentIndex > 0 ? products[currentIndex - 1].id : null;
      this.nextProductId = currentIndex < products.length - 1 ? products[currentIndex + 1].id : null;
    });
  }

  gotoPreviousProduct(): void {
    if (this.prevProductId) {
      this.router.navigate(['/products', this.prevProductId]);
    }
  }

  gotoNextProduct(): void {
    if (this.nextProductId) {
      this.router.navigate(['/products', this.nextProductId]);
    }
  }

  goBackToList(): void {
    // Method 1: Simple navigation
    // this.router.navigate(['/products']);

    // Method 2: Use Location service to go back to previous page
    window.history.back();
  }
}
<!-- components/products/product-detail/product-detail.component.html -->

<div class="container" *ngIf="product">
  <div class="row">
    <div class="col">
      <nav aria-label="breadcrumb">
        <ol class="breadcrumb">
          <li class="breadcrumb-item"><a routerLink="/">Home</a></li>
          <li class="breadcrumb-item"><a routerLink="/products">Products</a></li>
          <li class="breadcrumb-item active" aria-current="page">{{ product.name }}</li>
        </ol>
      </nav>
    </div>
  </div>

  <div class="row">
    <div class="col-md-6">
      <img [src]="product.imageUrl" class="img-fluid" alt="{{ product.name }}">
    </div>
    <div class="col-md-6">
      <h1>{{ product.name }}</h1>
      <p class="lead">{{ product.description }}</p>
      <p class="display-6">{{ product.price | currency }}</p>
      <p>Category: <span class="badge bg-primary">{{ product.category | titlecase }}</span></p>

      <div class="d-grid gap-2 d-md-block">
        <button class="btn btn-primary me-md-2" type="button">Add to Cart</button>
        <a [routerLink]="['edit']" class="btn btn-outline-secondary" type="button">Edit Product</a>
      </div>

      <hr>

      <h3>Navigation Examples</h3>
      <div class="mb-3">
        <h5>Navigate with RouterLink:</h5>
        <div class="btn-group">
          <a [routerLink]="['/products', prevProductId]" class="btn btn-outline-primary" [class.disabled]="!prevProductId">Previous Product</a>
          <a [routerLink]="['/products', nextProductId]" class="btn btn-outline-primary" [class.disabled]="!nextProductId">Next Product</a>
        </div>
      </div>

      <div>
        <h5>Navigate Programmatically:</h5>
        <div class="btn-group">
          <button (click)="gotoPreviousProduct()" class="btn btn-outline-secondary" [disabled]="!prevProductId">
            Previous (Router Navigate)
          </button>
          <button (click)="gotoNextProduct()" class="btn btn-outline-secondary" [disabled]="!nextProductId">
            Next (Router Navigate)
          </button>
        </div>
      </div>

      <div class="mt-3">
        <button (click)="goBackToList()" class="btn btn-link">Back to Products</button>
      </div>
    </div>
  </div>
</div>

<div class="container" *ngIf="!product?.id">
  <div class="alert alert-warning" role="alert">
    Product not found. <a routerLink="/products" class="alert-link">Return to product list</a>
  </div>
</div>
