// components/shared/navbar/navbar.component.ts

import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from '../../../services/auth.service';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {

  constructor(
    public authService: AuthService,
    private router: Router
  ) { }

  logout() {
    this.authService.logout();
    this.router.navigate(['/login']);
  }
}
<!-- components/shared/navbar/navbar.component.html -->

<nav class="navbar navbar-expand-lg navbar-light bg-light">
  <div class="container">
    <a class="navbar-brand" routerLink="/">Angular Shop</a>

    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarNav">
      <span class="navbar-toggler-icon"></span>
    </button>

    <div class="collapse navbar-collapse" id="navbarNav">
      <ul class="navbar-nav me-auto">
        <li class="nav-item">
          <a class="nav-link" routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}">Home</a>
        </li>
        <li class="nav-item">
          <a class="nav-link" routerLink="/products" routerLinkActive="active">Products</a>
        </li>
        <li class="nav-item" *ngIf="authService.isAuthenticated()">
          <a class="nav-link" routerLink="/admin" routerLinkActive="active">Admin</a>
        </li>
        <li class="nav-item" *ngIf="authService.isAuthenticated()">
          <a class="nav-link" routerLink="/user/profile" routerLinkActive="active">My Profile</a>
        </li>
      </ul>

      <div class="d-flex">
        <ng-container *ngIf="!authService.isAuthenticated(); else loggedIn">
          <button class="btn btn-outline-primary" routerLink="/login">Login</button>
        </ng-container>

        <ng-template #loggedIn>
          <span class="me-3">Welcome, {{ authService.getCurrentUser()?.name }}</span>
          <button class="btn btn-outline-danger" (click)="logout()">Logout</button>
        </ng-template>
      </div>
    </div>
  </div>
</nav>
