import { Component, Inject } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';

export interface ConfirmDialogData {
  title: string;
  message: string;
  confirmButtonText: string;
  cancelButtonText: string;
  color?: 'primary' | 'accent' | 'warn';
}

@Component({
  selector: 'app-confirm-dialog',
  template: `
    <h2 mat-dialog-title>{{ data.title }}</h2>
    <mat-dialog-content>
      <p [innerHTML]="data.message"></p>
    </mat-dialog-content>
    <mat-dialog-actions align="end">
      <button mat-button mat-dialog-close>
        {{ data.cancelButtonText || 'Cancel' }}
      </button>
      <button mat-raised-button 
              [color]="data.color || 'primary'" 
              [mat-dialog-close]="true">
        {{ data.confirmButtonText || 'Confirm' }}
      </button>
    </mat-dialog-actions>
  `,
  styles: [`
    :host {
      // Modern Design System - CSS Custom Properties
      --dialog-background: rgba(255, 255, 255, 0.98);
      --dialog-backdrop-filter: blur(20px) saturate(180%);
      --dialog-border-radius: 20px;
      --dialog-shadow: 0 32px 64px rgba(0, 0, 0, 0.15);
      --dialog-title-color: #1f2937;
      --dialog-content-color: #4b5563;
      --dialog-transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      --dialog-button-gap: 1rem;
      --dialog-padding: 1.5rem;
    }

    ::ng-deep .mat-dialog-container {
      background: var(--dialog-background);
      backdrop-filter: var(--dialog-backdrop-filter);
      -webkit-backdrop-filter: var(--dialog-backdrop-filter);
      border-radius: var(--dialog-border-radius);
      box-shadow: var(--dialog-shadow);
      border: 1px solid rgba(255, 255, 255, 0.2);
      padding: 0;
      overflow: hidden;
      animation: dialogSlideIn 0.4s ease-out;
    }

    ::ng-deep .mat-dialog-title {
      margin: 0;
      padding: var(--dialog-padding) var(--dialog-padding) 0;
      font-size: 1.5rem;
      font-weight: 700;
      color: var(--dialog-title-color);
      background: linear-gradient(
        135deg,
        #1f2937 0%,
        #374151 100%
      );
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      position: relative;

      &::after {
        content: '';
        position: absolute;
        bottom: -8px;
        left: var(--dialog-padding);
        right: var(--dialog-padding);
        height: 2px;
        background: linear-gradient(90deg, #3f51b5, #ff4081);
        border-radius: 1px;
        opacity: 0.8;
      }
    }

    mat-dialog-content {
      min-width: 350px;
      max-width: 500px;
      padding: 1.5rem var(--dialog-padding) 1rem;

      p {
        margin: 0;
        font-size: 1rem;
        line-height: 1.6;
        color: var(--dialog-content-color);
        font-weight: 400;
      }
    }

    ::ng-deep mat-dialog-actions {
      padding: 1rem var(--dialog-padding) var(--dialog-padding);
      margin: 0;
      gap: var(--dialog-button-gap);
      background: rgba(248, 250, 252, 0.8);
      backdrop-filter: blur(8px);
      border-top: 1px solid rgba(0, 0, 0, 0.05);

      button {
        border-radius: 12px;
        font-weight: 500;
        text-transform: none;
        letter-spacing: 0.025em;
        padding: 0.75rem 1.5rem;
        transition: var(--dialog-transition);
        position: relative;
        overflow: hidden;

        // Cancel button styling
        &[mat-button] {
          background: rgba(107, 114, 128, 0.1);
          color: #6b7280;
          border: 1px solid rgba(107, 114, 128, 0.2);

          &:hover {
            background: rgba(107, 114, 128, 0.15);
            color: #374151;
            transform: translateY(-1px);
            box-shadow: 0 4px 12px rgba(107, 114, 128, 0.2);
          }

          &:active {
            transform: translateY(0);
          }
        }

        // Confirm button styling
        &[mat-raised-button] {
          background: linear-gradient(135deg, #3f51b5, #ff4081);
          color: white;
          box-shadow: 0 4px 12px rgba(63, 81, 181, 0.3);

          // Shimmer effect
          &::before {
            content: '';
            position: absolute;
            top: 0;
            left: -100%;
            width: 100%;
            height: 100%;
            background: linear-gradient(
              90deg,
              transparent,
              rgba(255, 255, 255, 0.2),
              transparent
            );
            transition: left 0.6s ease;
          }

          &:hover {
            background: linear-gradient(135deg, #303f9f, #e91e63);
            transform: translateY(-2px);
            box-shadow: 0 8px 20px rgba(63, 81, 181, 0.4);

            &::before {
              left: 100%;
            }
          }

          &:active {
            transform: translateY(0);
          }

          // Color variations
          &[color="warn"] {
            background: linear-gradient(135deg, #ef4444, #dc2626);
            box-shadow: 0 4px 12px rgba(239, 68, 68, 0.3);

            &:hover {
              background: linear-gradient(135deg, #dc2626, #b91c1c);
              box-shadow: 0 8px 20px rgba(239, 68, 68, 0.4);
            }
          }

          &[color="accent"] {
            background: linear-gradient(135deg, #ff4081, #e91e63);
            box-shadow: 0 4px 12px rgba(255, 64, 129, 0.3);

            &:hover {
              background: linear-gradient(135deg, #e91e63, #c2185b);
              box-shadow: 0 8px 20px rgba(255, 64, 129, 0.4);
            }
          }
        }

        &:focus {
          outline: none;
          box-shadow: 
            0 4px 12px rgba(63, 81, 181, 0.3),
            0 0 0 3px rgba(63, 81, 181, 0.1);
        }
      }
    }

    // Animation Keyframes
    @keyframes dialogSlideIn {
      0% {
        opacity: 0;
        transform: translateY(-30px) scale(0.95);
      }
      100% {
        opacity: 1;
        transform: translateY(0) scale(1);
      }
    }

    // Responsive Design
    @media (max-width: 768px) {
      mat-dialog-content {
        min-width: 280px;
        max-width: 90vw;
      }

      ::ng-deep mat-dialog-actions {
        flex-direction: column;
        align-items: stretch;

        button {
          width: 100%;
          margin: 0;
        }
      }
    }

    // Dark mode support
    @media (prefers-color-scheme: dark) {
      :host {
        --dialog-background: rgba(31, 41, 55, 0.95);
        --dialog-title-color: #f9fafb;
        --dialog-content-color: #d1d5db;
      }

      ::ng-deep .mat-dialog-container {
        border: 1px solid rgba(255, 255, 255, 0.1);
      }

      ::ng-deep .mat-dialog-title {
        background: linear-gradient(
          135deg,
          #f9fafb 0%,
          #e5e7eb 100%
        );
        background-clip: text;
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
      }

      ::ng-deep mat-dialog-actions {
        background: rgba(17, 24, 39, 0.8);
        border-top: 1px solid rgba(255, 255, 255, 0.1);

        button[mat-button] {
          background: rgba(156, 163, 175, 0.1);
          color: #d1d5db;
          border: 1px solid rgba(156, 163, 175, 0.2);

          &:hover {
            background: rgba(156, 163, 175, 0.2);
            color: #f9fafb;
          }
        }
      }
    }

    // High contrast support
    @media (prefers-contrast: high) {
      :host {
        --dialog-background: #ffffff;
        --dialog-title-color: #000000;
        --dialog-content-color: #000000;
      }

      ::ng-deep .mat-dialog-container {
        border: 2px solid #000000;
      }

      ::ng-deep .mat-dialog-title {
        -webkit-text-fill-color: #000000;
        color: #000000;
      }

      ::ng-deep mat-dialog-actions {
        background: #f8f9fa;
        border-top: 2px solid #000000;

        button {
          border: 2px solid #000000;

          &[mat-button] {
            background: #ffffff;
            color: #000000;
          }

          &[mat-raised-button] {
            background: #000000;
            color: #ffffff;
          }
        }
      }
    }

    // Reduced motion support
    @media (prefers-reduced-motion: reduce) {
      ::ng-deep .mat-dialog-container {
        animation: none;
      }

      ::ng-deep mat-dialog-actions button {
        &:hover {
          transform: none;
        }

        &:active {
          transform: none;
        }

        &[mat-raised-button]::before {
          transition: none;
        }
      }
    }
  `]
})
export class ConfirmDialogComponent {
  constructor(
    public dialogRef: MatDialogRef<ConfirmDialogComponent>,
    @Inject(MAT_DIALOG_DATA) public data: ConfirmDialogData
  ) {}
}
