import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-newsletter-signup',
  template: `
    <section class="newsletter-section">
      <div class="newsletter-container">
        <div class="newsletter-content">
          <h2>Subscribe to Our Newsletter</h2>
          <p>Stay updated with our latest products, exclusive offers, and shopping tips.</p>

          <form [formGroup]="newsletterForm" (ngSubmit)="onSubmit()" class="newsletter-form">
            <div class="form-group">
              <input 
                type="email" 
                formControlName="email" 
                placeholder="Your email address"
                [class.invalid]="submitted && f['email'].errors"
              >
              <button type="submit" [disabled]="loading">
                <span *ngIf="!loading">Subscribe</span>
                <span *ngIf="loading">Subscribing...</span>
              </button>
            </div>

            <div class="error-message" *ngIf="submitted && f['email'].errors">
              <span *ngIf="f['email'].errors['required']">Email is required</span>
              <span *ngIf="f['email'].errors['email']">Please enter a valid email address</span>
            </div>

            <div class="success-message" *ngIf="success">
              Thank you for subscribing to our newsletter!
            </div>
          </form>

          <p class="privacy-note">By subscribing, you agree to our Privacy Policy and consent to receive our promotional emails.</p>
        </div>
      </div>
    </section>
  `,
  styles: [`
    :host {
      // Modern Design System - CSS Custom Properties
      --newsletter-section-padding: clamp(4rem, 8vw, 6rem);
      --newsletter-container-max-width: 900px;
      --newsletter-background: linear-gradient(
        135deg,
        #667eea 0%,
        #764ba2 100%
      );
      --newsletter-overlay: linear-gradient(
        135deg,
        rgba(102, 126, 234, 0.95) 0%,
        rgba(118, 75, 162, 0.9) 100%
      );
      --newsletter-content-background: rgba(255, 255, 255, 0.1);
      --newsletter-backdrop-filter: blur(20px) saturate(180%);
      --newsletter-border-radius: 24px;
      --newsletter-form-border-radius: 50px;
      --newsletter-shadow: 0 32px 64px rgba(0, 0, 0, 0.15);
      --newsletter-transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      --newsletter-button-gradient: linear-gradient(135deg, #ff4081, #3f51b5);
      --newsletter-success-color: #10b981;
      --newsletter-error-color: #ef4444;
    }

    .newsletter-section {
      background: var(--newsletter-background);
      padding: var(--newsletter-section-padding) clamp(1rem, 4vw, 2rem);
      position: relative;
      overflow: hidden;

      // Animated background pattern
      &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: var(--newsletter-overlay);
        z-index: 1;
      }

      &::after {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: 
          radial-gradient(circle at 20% 80%, rgba(255, 255, 255, 0.1) 0%, transparent 50%),
          radial-gradient(circle at 80% 20%, rgba(255, 255, 255, 0.08) 0%, transparent 50%);
        animation: backgroundFloat 6s ease-in-out infinite alternate;
        z-index: 2;
      }
    }

    .newsletter-container {
      max-width: var(--newsletter-container-max-width);
      margin: 0 auto;
      text-align: center;
      position: relative;
      z-index: 3;
    }

    .newsletter-content {
      background: var(--newsletter-content-background);
      backdrop-filter: var(--newsletter-backdrop-filter);
      -webkit-backdrop-filter: var(--newsletter-backdrop-filter);
      border-radius: var(--newsletter-border-radius);
      padding: clamp(2.5rem, 5vw, 4rem);
      border: 1px solid rgba(255, 255, 255, 0.2);
      box-shadow: var(--newsletter-shadow);
      transition: var(--newsletter-transition);

      &:hover {
        transform: translateY(-4px);
        box-shadow: 0 40px 80px rgba(0, 0, 0, 0.2);
      }

      h2 {
        margin-bottom: 1rem;
        font-size: clamp(1.75rem, 4vw, 2.5rem);
        font-weight: 700;
        color: white;
        text-shadow: 0 4px 12px rgba(0, 0, 0, 0.3);
        position: relative;

        &::after {
          content: '';
          position: absolute;
          bottom: -8px;
          left: 50%;
          transform: translateX(-50%);
          width: 60px;
          height: 3px;
          background: linear-gradient(90deg, #ff4081, #3f51b5);
          border-radius: 2px;
          animation: titleUnderlineGrow 1.5s ease-out;
        }
      }

      > p {
        margin-bottom: 2.5rem;
        color: rgba(255, 255, 255, 0.9);
        font-size: clamp(1rem, 2.5vw, 1.2rem);
        line-height: 1.6;
        text-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
      }
    }

    .newsletter-form {
      margin-bottom: 1.5rem;
    }

    .form-group {
      display: flex;
      max-width: 500px;
      margin: 0 auto 1rem;
      position: relative;
      border-radius: var(--newsletter-form-border-radius);
      overflow: hidden;
      box-shadow: 0 12px 24px rgba(0, 0, 0, 0.15);

      &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background: linear-gradient(135deg, rgba(255, 255, 255, 0.2), rgba(255, 255, 255, 0.1));
        z-index: 1;
        border-radius: var(--newsletter-form-border-radius);
      }
    }

    input[type="email"] {
      flex: 1;
      padding: 1rem 1.5rem;
      border: none;
      background: rgba(255, 255, 255, 0.9);
      backdrop-filter: blur(8px);
      font-size: 1rem;
      color: #2d3748;
      border-radius: var(--newsletter-form-border-radius) 0 0 var(--newsletter-form-border-radius);
      transition: var(--newsletter-transition);
      position: relative;
      z-index: 2;

      &::placeholder {
        color: #718096;
        font-weight: 400;
      }

      &:focus {
        outline: none;
        background: rgba(255, 255, 255, 0.95);
        box-shadow: inset 0 0 0 2px rgba(255, 64, 129, 0.3);
      }

      &.invalid {
        background: rgba(255, 245, 245, 0.9);
        box-shadow: inset 0 0 0 2px rgba(239, 68, 68, 0.4);
      }
    }

    button {
      padding: 1rem 2rem;
      background: var(--newsletter-button-gradient);
      color: white;
      border: none;
      border-radius: 0 var(--newsletter-form-border-radius) var(--newsletter-form-border-radius) 0;
      font-size: 1rem;
      font-weight: 600;
      cursor: pointer;
      transition: var(--newsletter-transition);
      position: relative;
      z-index: 2;
      overflow: hidden;

      &::before {
        content: '';
        position: absolute;
        top: 0;
        left: -100%;
        width: 100%;
        height: 100%;
        background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
        transition: left 0.6s ease;
      }

      &:hover {
        transform: scale(1.02);
        box-shadow: 0 8px 16px rgba(255, 64, 129, 0.4);

        &::before {
          left: 100%;
        }
      }

      &:active {
        transform: scale(0.98);
      }

      &:disabled {
        background: linear-gradient(135deg, #94a3b8, #64748b);
        cursor: not-allowed;
        transform: none;

        &:hover {
          transform: none;
          box-shadow: none;
        }
      }
    }

    .error-message {
      color: #fca5a5;
      background: rgba(239, 68, 68, 0.1);
      backdrop-filter: blur(8px);
      padding: 0.75rem 1.25rem;
      border-radius: 12px;
      margin-top: 1rem;
      font-size: 0.9rem;
      border: 1px solid rgba(239, 68, 68, 0.2);
      animation: errorSlideIn 0.3s ease-out;
    }

    .success-message {
      color: #6ee7b7;
      background: rgba(16, 185, 129, 0.15);
      backdrop-filter: blur(8px);
      padding: 1rem 1.5rem;
      border-radius: 12px;
      margin-top: 1rem;
      font-size: 1rem;
      font-weight: 500;
      border: 1px solid rgba(16, 185, 129, 0.3);
      animation: successSlideIn 0.5s ease-out;

      &::before {
        content: '✓ ';
        font-weight: bold;
        margin-right: 0.5rem;
      }
    }

    .privacy-note {
      font-size: 0.85rem;
      color: rgba(255, 255, 255, 0.7);
      margin-top: 1.5rem;
      line-height: 1.5;
      text-shadow: 0 1px 4px rgba(0, 0, 0, 0.2);
    }

    // Animation Keyframes
    @keyframes backgroundFloat {
      0% {
        transform: translateY(0) rotate(0deg);
        opacity: 0.8;
      }
      100% {
        transform: translateY(-10px) rotate(1deg);
        opacity: 1;
      }
    }

    @keyframes titleUnderlineGrow {
      0% {
        width: 0;
        opacity: 0;
      }
      100% {
        width: 60px;
        opacity: 1;
      }
    }

    @keyframes errorSlideIn {
      0% {
        opacity: 0;
        transform: translateY(-10px);
      }
      100% {
        opacity: 1;
        transform: translateY(0);
      }
    }

    @keyframes successSlideIn {
      0% {
        opacity: 0;
        transform: translateY(-10px) scale(0.95);
      }
      100% {
        opacity: 1;
        transform: translateY(0) scale(1);
      }
    }

    // Responsive Design
    @media (max-width: 768px) {
      .form-group {
        flex-direction: column;
        border-radius: 16px;
      }

      input[type="email"] {
        border-radius: 16px 16px 0 0;
        margin-bottom: 0;
      }

      button {
        border-radius: 0 0 16px 16px;
        padding: 1rem;
      }
    }

    @media (max-width: 576px) {
      :host {
        --newsletter-section-padding: clamp(3rem, 6vw, 4rem);
        --newsletter-border-radius: 16px;
      }

      .newsletter-content {
        padding: clamp(1.5rem, 4vw, 2rem);
      }
    }

    // Accessibility
    @media (prefers-reduced-motion: reduce) {
      :host {
        --newsletter-transition: none;
      }

      .newsletter-section::after {
        animation: none;
      }

      .newsletter-content h2::after {
        animation: none;
      }

      .error-message {
        animation: none;
      }

      .success-message {
        animation: none;
      }
    }

    @media (prefers-contrast: high) {
      .newsletter-content {
        background: rgba(0, 0, 0, 0.8);
        border: 2px solid rgba(255, 255, 255, 0.5);
      }

      input[type="email"] {
        background: rgba(255, 255, 255, 0.95);
        border: 1px solid #000;
      }
    }
  `]
})
export class NewsletterSignupComponent {
  newsletterForm: FormGroup;
  loading = false;
  submitted = false;
  success = false;

  constructor(private fb: FormBuilder) {
    this.newsletterForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]]
    });
  }

  get f() { return this.newsletterForm.controls; }

  onSubmit() {
    this.submitted = true;

    if (this.newsletterForm.invalid) {
      return;
    }

    this.loading = true;

    // Simulate API call
    setTimeout(() => {
      this.loading = false;
      this.success = true;
      this.newsletterForm.reset();
      this.submitted = false;

      // Reset success message after 5 seconds
      setTimeout(() => {
        this.success = false;
      }, 5000);
    }, 1500);
  }
}
