import { createReducer, on } from '@ngrx/store';

export interface Product {
  id: number;
  name: string;
  description: string;
  price: number;
  image: string;
  category: string;
  inStock: boolean;
}

export interface State {
  products: Product[];
  selectedProduct: Product | null;
  loading: boolean;
  error: string | null;
}

export const initialState: State = {
  products: [],
  selectedProduct: null,
  loading: false,
  error: null
};

export const reducer = createReducer(
  initialState
  // Add actions later
);
