import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';

import { AppState } from '../../../../store';
import { Product } from '../../../../core/models/product.model';

@Component({
  selector: 'app-featured-products',
  templateUrl: './featured-products.component.html',
  styleUrls: ['./featured-products.component.scss']
})
export class FeaturedProductsComponent implements OnInit {
  @Input() title = 'Featured Products';
  @Input() showViewAll = true;
  @Input() viewAllLink = '/products';
  @Input() limit = 8;

  @Output() addToCart = new EventEmitter<Product>();
  @Output() addToWishlist = new EventEmitter<Product>();
  @Output() viewDetails = new EventEmitter<Product>();

  products$: Observable<Product[]>;
  loading$: Observable<boolean>;

  constructor(private store: Store<AppState>) {
    this.loading$ = this.store.select(state => state.ui.loading);
    this.products$ = this.store.select(state => {
      let filteredProducts = state.products.items.filter(product => product.featured);
      return filteredProducts.slice(0, this.limit);
    });
  }

  ngOnInit(): void {
    // Any initialization logic if needed
  }

  onAddToCart(product: Product): void {
    this.addToCart.emit(product);
  }

  onAddToWishlist(product: Product): void {
    this.addToWishlist.emit(product);
  }

  onViewDetails(product: Product): void {
    this.viewDetails.emit(product);
  }
}
<div class="featured-products">
  <div class="featured-header">
    <h2 class="featured-title">{{ title }}</h2>
    <a *ngIf="showViewAll" [routerLink]="viewAllLink" class="view-all-link">
      View All <mat-icon>chevron_right</mat-icon>
    </a>
  </div>

  <div class="products-grid">
    <ng-container *ngIf="(products$ | async) as products">
      <div *ngIf="products.length > 0; else noProducts" class="products-container">
        <div *ngFor="let product of products" class="product-card-wrapper">
          <app-product-card 
            [product]="product"
            (addToCart)="onAddToCart($event)"
            (addToWishlist)="onAddToWishlist($event)"
            (viewDetails)="onViewDetails($event)">
          </app-product-card>
        </div>
      </div>

      <ng-template #noProducts>
        <div class="no-products-message">
          <mat-icon>info</mat-icon>
          <p>No featured products available at the moment.</p>
        </div>
      </ng-template>
    </ng-container>

    <!-- Loading State -->
    <div *ngIf="loading$ | async" class="loading-container">
      <app-loading-spinner [diameter]="40"></app-loading-spinner>
    </div>
  </div>
</div>
