import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Store } from '@ngrx/store';
import { CartItem } from '../../core/models/cart-item.model';
import { updateCartItemQuantity, removeFromCart, clearCart } from '../../store/actions/cart.actions';
import { selectCartItems, selectCartTotal, selectCartItemCount } from '../../store/selectors/cart.selectors';

@Component({
  selector: 'app-cart',
  template: `
    <div class="cart-container">
      <h2>Shopping Cart</h2>

      <div *ngIf="cartItems$ | async as cartItems; else emptyCart">
        <div class="cart-summary">
          <p>Items: {{ itemCount$ | async }}</p>
          <p>Total: {{ total$ | async | currency }}</p>
        </div>

        <div class="cart-items">
          <div *ngFor="let item of cartItems" class="cart-item">
            <img [src]="item.image" [alt]="item.name" class="item-image">
            <div class="item-details">
              <h4>{{ item.name }}</h4>
              <p>Price: {{ item.price | currency }}</p>
              <div class="quantity-controls">
                <button (click)="decreaseQuantity(item)" [disabled]="item.quantity <= 1">-</button>
                <span>{{ item.quantity }}</span>
                <button (click)="increaseQuantity(item)">+</button>
              </div>
              <button (click)="removeItem(item)" class="remove-btn">Remove</button>
            </div>
          </div>
        </div>

        <div class="cart-actions">
          <button (click)="clearCart()" class="clear-btn">Clear Cart</button>
          <button routerLink="/checkout" class="checkout-btn">Proceed to Checkout</button>
        </div>
      </div>

      <ng-template #emptyCart>
        <div class="empty-cart">
          <p>Your cart is empty</p>
          <button routerLink="/products" class="continue-shopping">Continue Shopping</button>
        </div>
      </ng-template>
    </div>
  `,
  styles: [`
    .cart-container {
      max-width: 800px;
      margin: 0 auto;
      padding: 20px;
    }

    .cart-item {
      display: flex;
      gap: 15px;
      padding: 15px;
      border-bottom: 1px solid #eee;
    }

    .item-image {
      width: 80px;
      height: 80px;
      object-fit: cover;
    }

    .item-details {
      flex: 1;
    }

    .quantity-controls {
      display: flex;
      align-items: center;
      gap: 10px;
      margin: 10px 0;
    }

    .quantity-controls button {
      width: 30px;
      height: 30px;
      border: 1px solid #ccc;
      background: white;
      cursor: pointer;
    }

    .cart-actions {
      margin-top: 20px;
      display: flex;
      gap: 15px;
    }

    .checkout-btn {
      background: #007bff;
      color: white;
      padding: 10px 20px;
      border: none;
      cursor: pointer;
    }

    .clear-btn, .remove-btn {
      background: #dc3545;
      color: white;
      padding: 5px 10px;
      border: none;
      cursor: pointer;
    }

    .empty-cart {
      text-align: center;
      padding: 40px;
    }

    .continue-shopping {
      background: #28a745;
      color: white;
      padding: 10px 20px;
      border: none;
      cursor: pointer;
    }
  `]
})
export class CartComponent implements OnInit {
  cartItems$: Observable<CartItem[]>;
  total$: Observable<number>;
  itemCount$: Observable<number>;

  constructor(private store: Store) {
    this.cartItems$ = this.store.select(selectCartItems);
    this.total$ = this.store.select(selectCartTotal);
    this.itemCount$ = this.store.select(selectCartItemCount);
  }

  ngOnInit(): void {}

  increaseQuantity(item: CartItem): void {
    this.store.dispatch(updateCartItemQuantity({
      productId: item.productId,
      quantity: item.quantity + 1
    }));
  }

  decreaseQuantity(item: CartItem): void {
    if (item.quantity > 1) {
      this.store.dispatch(updateCartItemQuantity({
        productId: item.productId,
        quantity: item.quantity - 1
      }));
    }
  }

  removeItem(item: CartItem): void {
    this.store.dispatch(removeFromCart({ productId: item.productId }));
  }

  clearCart(): void {
    this.store.dispatch(clearCart());
  }
}
