import { 
  Component, 
  Input, 
  Output, 
  EventEmitter, 
  OnInit, 
  OnDestroy, 
  ChangeDetectionStrategy,
  ChangeDetectorRef,
  HostListener,
  Inject,
  Optional
} from '@angular/core';
import { Subject, interval, takeUntil, of } from 'rxjs';
import { Product } from '../../../core/models/product.model';

@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProductCardComponent implements OnInit, OnDestroy {
  @Input() product!: Product;
  @Input() showActions = true;
  @Input() showQuantitySelector = false;
  @Input() showBuyNow = false;
  @Input() showPriceDetails = true;
  @Input() showShippingInfo = true;
  @Input() loading = 'lazy' as 'lazy' | 'eager';
  @Input() size: 'small' | 'medium' | 'large' = 'medium';
  @Input() layout: 'vertical' | 'horizontal' = 'vertical';

  @Output() addToCart = new EventEmitter<{product: Product, quantity: number}>();
  @Output() addToWishlist = new EventEmitter<Product>();
  @Output() removeFromWishlist = new EventEmitter<Product>();
  @Output() addToCompare = new EventEmitter<Product>();
  @Output() removeFromCompare = new EventEmitter<Product>();
  @Output() viewDetails = new EventEmitter<Product>();
  @Output() quickView = new EventEmitter<Product>();
  @Output() buyNow = new EventEmitter<{product: Product, quantity: number}>();
  @Output() imageClick = new EventEmitter<{product: Product, imageIndex: number}>();

  // Component state
  private destroy$ = new Subject<void>();
  discountPercentage: number = 0;
  discountAmount: number = 0;
  selectedQuantity = 1;
  currentImageIndex = 0;
  addingToCart = false;
  imageLoading = true;
  imageError = false;

  // Product status properties
  isNewArrival = false;
  isBestSeller = false;
  isLowStock = false;
  isInWishlist = false;
  isInCompare = false;

  constructor(
    private cdr: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.calculateDiscounts();
    this.checkProductStatus();
    this.initializeUserInteractions();
    this.startImageCarousel();
  }

  ngOnDestroy(): void {
    this.destroy$.next();
    this.destroy$.complete();
  }

  @HostListener('mouseenter')
  onMouseEnter(): void {
    // Optional: Preload additional product data on hover
  }

  @HostListener('mouseleave')
  onMouseLeave(): void {
    // Reset any hover states if needed
  }

  private calculateDiscounts(): void {
    if (this.product.regularPrice && this.product.regularPrice > this.product.price) {
      this.discountPercentage = Math.round(
        ((this.product.regularPrice - this.product.price) / this.product.regularPrice) * 100
      );
      this.discountAmount = this.product.regularPrice - this.product.price;
    }
  }

  private checkProductStatus(): void {
    const now = new Date();
    const productDate = new Date(this.product.createdAt || now);
    const daysDiff = Math.floor((now.getTime() - productDate.getTime()) / (1000 * 60 * 60 * 24));

    // Check if new arrival (less than 30 days old)
    this.isNewArrival = daysDiff <= 30;

    // Check if bestseller (based on sales or rating)
    this.isBestSeller = this.product.featured || 
                       (this.product.rating >= 4.5 && (this.product.reviews?.length || 0) >= 100);

    // Check if low stock
    this.isLowStock = this.product.stockQuantity > 0 && this.product.stockQuantity <= 10;
  }

  private initializeUserInteractions(): void {
    // These would be connected to actual services in a real implementation
    // For now, we'll use mock values
    this.isInWishlist = false;
    this.isInCompare = false;
  }

  private startImageCarousel(): void {
    if (this.product.images && this.product.images.length > 1) {
      // Auto-rotate images on hover (optional)
      interval(3000)
        .pipe(takeUntil(this.destroy$))
        .subscribe(() => {
          if (this.product.images && this.product.images.length > 1) {
            this.currentImageIndex = (this.currentImageIndex + 1) % this.product.images.length;
            this.cdr.markForCheck();
          }
        });
    }
  }

  // Event handlers
  onAddToCart(): void {
    if (this.product.stockQuantity === 0 || this.addingToCart) return;

    this.addingToCart = true;
    this.cdr.markForCheck();

    // Simulate API call delay
    setTimeout(() => {
      this.addToCart.emit({
        product: this.product,
        quantity: this.selectedQuantity
      });
      this.addingToCart = false;
      this.cdr.markForCheck();
    }, 500);
  }

  onToggleWishlist(event: Event): void {
    event.stopPropagation();
    event.preventDefault();

    if (this.isInWishlist) {
      this.removeFromWishlist.emit(this.product);
      this.isInWishlist = false;
    } else {
      this.addToWishlist.emit(this.product);
      this.isInWishlist = true;
    }
    this.cdr.markForCheck();
  }

  onToggleCompare(event: Event): void {
    event.stopPropagation();
    event.preventDefault();

    if (this.isInCompare) {
      this.removeFromCompare.emit(this.product);
      this.isInCompare = false;
    } else {
      this.addToCompare.emit(this.product);
      this.isInCompare = true;
    }
    this.cdr.markForCheck();
  }

  onQuickView(event: Event): void {
    event.stopPropagation();
    event.preventDefault();
    this.quickView.emit(this.product);
  }

  onViewDetails(): void {
    this.viewDetails.emit(this.product);
  }

  onBuyNow(): void {
    if (this.product.stockQuantity === 0) return;

    this.buyNow.emit({
      product: this.product,
      quantity: this.selectedQuantity
    });
  }

  onQuantityChange(quantity: number): void {
    this.selectedQuantity = quantity;
  }

  changeImage(index: number, event: Event): void {
    event.stopPropagation();
    event.preventDefault();
    this.currentImageIndex = index;
    this.imageClick.emit({
      product: this.product,
      imageIndex: index
    });
  }

  onImageLoad(): void {
    this.imageLoading = false;
    this.imageError = false;
    this.cdr.markForCheck();
  }

  onImageError(): void {
    this.imageLoading = false;
    this.imageError = true;
    this.cdr.markForCheck();
  }

  // Getter methods for template
  get currentImage(): string {
    if (this.product.images && this.product.images.length > 0) {
      return this.product.images[this.currentImageIndex] || this.product.imageUrl;
    }
    return this.product.imageUrl;
  }

  get stockStatus(): 'in-stock' | 'low-stock' | 'out-of-stock' {
    if (this.product.stockQuantity === 0) return 'out-of-stock';
    if (this.product.stockQuantity <= 10) return 'low-stock';
    return 'in-stock';
  }

  get cardClasses(): string[] {
    const classes = [`product-card--${this.size}`, `product-card--${this.layout}`];

    if (this.product.featured) classes.push('product-card--featured');
    if (this.isNewArrival) classes.push('product-card--new');
    if (this.isBestSeller) classes.push('product-card--bestseller');
    if (this.discountPercentage > 0) classes.push('product-card--on-sale');

    return classes;
  }
}
<div class="product-card" 
     [class.out-of-stock]="product.stockQuantity === 0"
     [class.featured]="product.featured"
     [class.new-arrival]="isNewArrival"
     [class.best-seller]="isBestSeller">

  <!-- Product Badges -->
  <div class="product-badges">
    <div class="badge discount-badge" *ngIf="discountPercentage > 0">
      <mat-icon>local_offer</mat-icon>
      <span>{{discountPercentage}}% OFF</span>
    </div>
    <div class="badge new-badge" *ngIf="isNewArrival">
      <mat-icon>fiber_new</mat-icon>
      <span>NEW</span>
    </div>
    <div class="badge bestseller-badge" *ngIf="isBestSeller">
      <mat-icon>star</mat-icon>
      <span>BESTSELLER</span>
    </div>
    <div class="badge featured-badge" *ngIf="product.featured">
      <mat-icon>verified</mat-icon>
      <span>FEATURED</span>
    </div>
    <div class="badge sold-out-badge" *ngIf="product.stockQuantity === 0">
      <mat-icon>block</mat-icon>
      <span>SOLD OUT</span>
    </div>
    <div class="badge low-stock-badge" *ngIf="isLowStock">
      <mat-icon>warning</mat-icon>
      <span>LOW STOCK</span>
    </div>
  </div>

  <!-- Quick Action Buttons -->
  <div class="quick-actions">
    <button mat-mini-fab
            class="quick-action-btn wishlist-btn"
            [class.active]="isInWishlist"
            (click)="onToggleWishlist($event)"
            [attr.aria-label]="isInWishlist ? 'Remove from wishlist' : 'Add to wishlist'"
            matTooltip="{{isInWishlist ? 'Remove from wishlist' : 'Add to wishlist'}}">
      <mat-icon>{{isInWishlist ? 'favorite' : 'favorite_border'}}</mat-icon>
    </button>

    <button mat-mini-fab
            class="quick-action-btn compare-btn"
            [class.active]="isInCompare"
            (click)="onToggleCompare($event)"
            [attr.aria-label]="isInCompare ? 'Remove from compare' : 'Add to compare'"
            matTooltip="{{isInCompare ? 'Remove from compare' : 'Add to compare'}}">
      <mat-icon>{{isInCompare ? 'compare_arrows' : 'compare_arrows'}}</mat-icon>
    </button>

    <button mat-mini-fab
            class="quick-action-btn quick-view-btn"
            (click)="onQuickView($event)"
            aria-label="Quick view"
            matTooltip="Quick view">
      <mat-icon>visibility</mat-icon>
    </button>
  </div>

  <!-- Product Image Container -->
  <div class="product-image-container" (click)="onViewDetails()">
    <div class="image-overlay"></div>
    <img [src]="product.imageUrl" 
         [alt]="product.name" 
         class="product-image" 
         [loading]="loading"
         (load)="onImageLoad()"
         (error)="onImageError()">

    <!-- Multiple images indicator -->
    <div class="image-indicators" *ngIf="product.images && product.images.length > 1">
      <span class="indicator" 
            *ngFor="let image of product.images; let i = index"
            [class.active]="i === currentImageIndex"
            (click)="changeImage(i, $event)"></span>
    </div>

    <!-- Stock level indicator -->
    <div class="stock-indicator" *ngIf="product.stockQuantity > 0 && product.stockQuantity <= 10">
      <div class="stock-bar">
        <div class="stock-fill" [style.width.%]="(product.stockQuantity / 10) * 100"></div>
      </div>
      <span class="stock-text">Only {{product.stockQuantity}} left</span>
    </div>
  </div>

  <!-- Product Content -->
  <div class="product-content">
    <!-- Category and Brand -->
    <div class="product-meta">
      <span class="product-category">{{product.category.name}}</span>
      <span class="product-brand" *ngIf="product.brand">{{product.brand}}</span>
    </div>

    <!-- Product Name -->
    <h3 class="product-name" 
        (click)="onViewDetails()"
        [attr.aria-label]="product.name">
      {{product.name}}
    </h3>

    <!-- Rating and Reviews -->
    <div class="product-rating">
      <app-star-rating 
        [rating]="product.rating" 
        [reviewCount]="product.reviews?.length || 0"
        [size]="'small'"
        [showCount]="true">
      </app-star-rating>
    </div>

    <!-- Price Section -->
    <div class="product-price">
      <div class="price-main">
        <span class="current-price">{{product.price | currency}}</span>
        <span class="regular-price" *ngIf="product.regularPrice && product.regularPrice > product.price">
          {{product.regularPrice | currency}}
        </span>
      </div>
      <div class="price-details" *ngIf="showPriceDetails">
        <span class="savings" *ngIf="discountAmount > 0">
          Save {{discountAmount | currency}}
        </span>
        <span class="price-per-unit" *ngIf="product.unit">
          {{(product.price / product.unitQuantity) | currency}}/{{product.unit}}
        </span>
      </div>
    </div>

    <!-- Product Features -->
    <div class="product-features" *ngIf="product.keyFeatures && product.keyFeatures.length > 0">
      <span class="feature" *ngFor="let feature of product.keyFeatures.slice(0, 2)">
        <mat-icon class="feature-icon">check_circle</mat-icon>
        {{feature}}
      </span>
    </div>

    <!-- Shipping Info -->
    <div class="shipping-info" *ngIf="showShippingInfo">
      <div class="shipping-item" *ngIf="product.freeShipping">
        <mat-icon>local_shipping</mat-icon>
        <span>Free Shipping</span>
      </div>
      <div class="shipping-item" *ngIf="product.fastDelivery">
        <mat-icon>flash_on</mat-icon>
        <span>Fast Delivery</span>
      </div>
    </div>
  </div>

  <!-- Action Buttons -->
  <div class="product-actions" *ngIf="showActions">
    <button class="add-to-cart-btn"
            [class.loading]="addingToCart"
            (click)="onAddToCart()"
            [disabled]="product.stockQuantity === 0 || addingToCart"
            [attr.aria-label]="'Add ' + product.name + ' to cart'">
      <mat-icon *ngIf="!addingToCart">shopping_cart</mat-icon>
      <mat-spinner *ngIf="addingToCart" diameter="20"></mat-spinner>
      <span>{{addingToCart ? 'Adding...' : 'Add to Cart'}}</span>
    </button>

    <div class="quantity-controls" *ngIf="showQuantitySelector && product.stockQuantity > 0">
      <app-quantity-selector
        [value]="selectedQuantity"
        [max]="product.stockQuantity"
        [min]="1"
        (valueChange)="onQuantityChange($event)">
      </app-quantity-selector>
    </div>

    <button class="buy-now-btn"
            *ngIf="showBuyNow"
            (click)="onBuyNow()"
            [disabled]="product.stockQuantity === 0"
            [attr.aria-label]="'Buy ' + product.name + ' now'">
      <mat-icon>flash_on</mat-icon>
      <span>Buy Now</span>
    </button>
  </div>

  <!-- Loading Overlay -->
  <div class="loading-overlay" *ngIf="loading">
    <mat-spinner diameter="40"></mat-spinner>
  </div>
</div>
