import { Component, Output, EventEmitter } from '@angular/core';
import { FormControl } from '@angular/forms';

@Component({
  selector: 'app-hero-banner',
  templateUrl: './hero-banner.component.html',
  styleUrls: ['./hero-banner.component.scss']
})
export class HeroBannerComponent {
  @Output() search = new EventEmitter<string>();

  searchControl = new FormControl('');

  slides = [
    {
      title: 'Summer Collection 2025',
      subtitle: 'New Arrivals',
      description: 'Discover the latest trends for this season. Up to 40% off on selected items.',
      buttonText: 'Shop Now',
      buttonLink: '/products?category=summer',
      imageSrc: 'assets/images/banners/summer-collection.jpg'
    },
    {
      title: 'Tech Gadgets',
      subtitle: 'Latest Electronics',
      description: 'Explore our wide range of cutting-edge electronics and gadgets.',
      buttonText: 'Discover Now',
      buttonLink: '/products?category=electronics',
      imageSrc: 'assets/images/banners/tech-gadgets.jpg'
    },
    {
      title: 'Home Essentials',
      subtitle: 'Make Your Home Beautiful',
      description: 'Find everything you need to create your dream living space.',
      buttonText: 'Shop Collection',
      buttonLink: '/products?category=home',
      imageSrc: 'assets/images/banners/home-essentials.jpg'
    }
  ];

  currentSlideIndex = 0;

  constructor() {
    // Auto-rotate slides every 5 seconds
    setInterval(() => {
      this.nextSlide();
    }, 5000);
  }

  onSearch(): void {
    this.search.emit(this.searchControl.value || '');
    this.searchControl.setValue('');
  }

  previousSlide(): void {
    this.currentSlideIndex = this.currentSlideIndex === 0 ? 
      this.slides.length - 1 : 
      this.currentSlideIndex - 1;
  }

  nextSlide(): void {
    this.currentSlideIndex = (this.currentSlideIndex + 1) % this.slides.length;
  }

  goToSlide(index: number): void {
    this.currentSlideIndex = index;
  }
}
<div class="hero-banner">
  <div class="hero-slider">
    <div class="hero-slide" *ngFor="let slide of slides; let i = index" [class.active]="i === currentSlideIndex">
      <div class="slide-bg" [ngStyle]="{'background-image': 'url(' + slide.imageSrc + ')'}"></div>
      <div class="slide-content">
        <div class="container">
          <span class="slide-subtitle">{{slide.subtitle}}</span>
          <h1 class="slide-title">{{slide.title}}</h1>
          <p class="slide-description">{{slide.description}}</p>
          <a [routerLink]="slide.buttonLink" mat-raised-button color="primary" class="shop-now-btn">{{slide.buttonText}}</a>
        </div>
      </div>
    </div>

    <!-- Slider Controls -->
    <div class="slider-controls">
      <button mat-icon-button class="slider-arrow prev" (click)="previousSlide()">
        <mat-icon>chevron_left</mat-icon>
      </button>
      <div class="slider-dots">
        <span 
          class="slider-dot" 
          *ngFor="let slide of slides; let i = index" 
          [class.active]="i === currentSlideIndex"
          (click)="goToSlide(i)">
        </span>
      </div>
      <button mat-icon-button class="slider-arrow next" (click)="nextSlide()">
        <mat-icon>chevron_right</mat-icon>
      </button>
    </div>
  </div>

  <div class="search-bar-container">
    <div class="search-bar">
      <mat-form-field appearance="outline" class="search-input">
        <mat-label>Search for products</mat-label>
        <input matInput placeholder="What are you looking for?" [formControl]="searchControl" (keyup.enter)="onSearch()">
        <mat-icon matSuffix>search</mat-icon>
      </mat-form-field>
      <button mat-raised-button color="primary" class="search-button" (click)="onSearch()">Search</button>
    </div>
  </div>
</div>
