import { Component } from '@angular/core';

@Component({
  selector: 'app-promo-banner',
  templateUrl: './promo-banner.component.html',
  styleUrls: ['./promo-banner.component.scss']
})
export class PromoBannerComponent {
  promoData = {
    title: 'Summer Sale',
    subtitle: 'Special Offer',
    description: 'Get up to 40% off on selected items! Limited time offer.',
    buttonText: 'Shop Now',
    buttonLink: '/products?sale=true',
    backgroundImage: 'assets/images/banners/summer-sale.jpg'
  };
}
<div class="promo-banner" [ngStyle]="{'background-image': 'url(' + promoData.backgroundImage + ')'}">
  <div class="promo-overlay"></div>
  <div class="container">
    <div class="promo-content">
      <span class="promo-subtitle">{{promoData.subtitle}}</span>
      <h2 class="promo-title">{{promoData.title}}</h2>
      <p class="promo-description">{{promoData.description}}</p>
      <a [routerLink]="promoData.buttonLink" mat-raised-button color="accent" class="promo-button">
        {{promoData.buttonText}}
      </a>
    </div>
  </div>
</div>
