import { createReducer, on } from '@ngrx/store';

export interface CartItem {
  id: number;
  name: string;
  price: number;
  quantity: number;
  image: string;
}

export interface State {
  items: CartItem[];
  total: number;
  loading: boolean;
  error: string | null;
}

export const initialState: State = {
  items: [],
  total: 0,
  loading: false,
  error: null
};

export const reducer = createReducer(
  initialState
  // Add actions later
);
