import { Component, OnInit } from '@angular/core';
import { Observable } from 'rxjs';
import { Store } from '@ngrx/store';
import { CartItem } from '../../core/models/cart-item.model';
import { updateCartItemQuantity, removeFromCart, clearCart } from '../../store/actions/cart.actions';
import { selectCartItems, selectCartTotal, selectCartItemCount } from '../../store/selectors/cart.selectors';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.scss']
})
export class CartComponent implements OnInit {
  cartItems$: Observable<CartItem[]>;
  total$: Observable<number>;
  itemCount$: Observable<number>;

  constructor(private store: Store) {
    this.cartItems$ = this.store.select(selectCartItems);
    this.total$ = this.store.select(selectCartTotal);
    this.itemCount$ = this.store.select(selectCartItemCount);
  }

  ngOnInit(): void {}

  trackByItemId(index: number, item: CartItem): string {
    return item.productId;
  }

  updateQuantity(item: CartItem, quantity: number): void {
    if (quantity > 0) {
      this.store.dispatch(updateCartItemQuantity({ 
        productId: item.productId, 
        quantity 
      }));
    } else {
      this.removeItem(item);
    }
  }

  incrementQuantity(item: CartItem): void {
    this.store.dispatch(updateCartItemQuantity({ 
      productId: item.productId, 
      quantity: item.quantity + 1 
    }));
  }

  decrementQuantity(item: CartItem): void {
    if (item.quantity > 1) {
      this.store.dispatch(updateCartItemQuantity({ 
        productId: item.productId, 
        quantity: item.quantity - 1 
      }));
    } else {
      this.removeItem(item);
    }
  }

  removeItem(item: CartItem): void {
    this.store.dispatch(removeFromCart({ productId: item.productId }));
  }

  clearCart(): void {
    this.store.dispatch(clearCart());
  }
}
<div class="cart-container">
  <h2>Shopping Cart</h2>

  <div *ngIf="cartItems$ | async as cartItems; else emptyCart">
    <div class="cart-summary">
      <p>{{ (itemCount$ | async) || 0 }} Items in Cart</p>
      <p>Total: {{ total$ | async | currency }}</p>
    </div>

    <div class="cart-items">
      <div *ngFor="let item of cartItems; trackBy: trackByItemId" class="cart-item cart-item-enter">
        <img [src]="item.image" [alt]="item.name" class="item-image" loading="lazy">

        <div class="item-details">
          <h4>{{ item.name }}</h4>
          <div class="item-price">{{ item.price | currency }}</div>

          <div class="quantity-controls">
            <button 
              type="button"
              (click)="decrementQuantity(item)" 
              [disabled]="item.quantity <= 1"
              aria-label="Decrease quantity">
              −
            </button>
            <span>{{ item.quantity }}</span>
            <button 
              type="button"
              (click)="incrementQuantity(item)"
              aria-label="Increase quantity">
              +
            </button>
          </div>
        </div>

        <div class="item-actions">
          <div class="item-total">{{ (item.price * item.quantity) | currency }}</div>
          <button 
            type="button"
            (click)="removeItem(item)" 
            class="remove-btn"
            aria-label="Remove item from cart">
            Remove
          </button>
        </div>
      </div>
    </div>

    <div class="cart-actions">
      <button 
        type="button"
        (click)="clearCart()" 
        class="clear-cart-btn"
        [disabled]="!cartItems.length">
        Clear Cart
      </button>
      <button 
        type="button"
        routerLink="/checkout" 
        class="checkout-btn"
        [disabled]="!cartItems.length">
        Proceed to Checkout
      </button>
    </div>
  </div>

  <ng-template #emptyCart>
    <div class="empty-cart">
      <div class="empty-cart-icon">🛒</div>
      <h3>Your cart is empty</h3>
      <p>Discover amazing products and start building your perfect collection!</p>
      <button 
        type="button"
        routerLink="/products" 
        class="continue-shopping-btn">
        Continue Shopping
      </button>
    </div>
  </ng-template>
</div>
