import { createFeatureSelector, createSelector } from '@ngrx/store';
export const selectProductState = createFeatureSelector('products');
export const selectAllProducts = createSelector(selectProductState, state => state.items);
export const selectProductLoading = createSelector(selectProductState, state => state.loading);
export const selectProductError = createSelector(selectProductState, state => state.error);
export const selectFeaturedProducts = createSelector(selectAllProducts, products => products.filter(product => product.featured));
export const selectProductById = productId => createSelector(selectAllProducts, products => products.find(product => product.id === productId));
// Category Selectors (assuming categories are part of product state or their own feature state)
export const selectAllCategories = createSelector(selectProductState,
// Or a dedicated category state selector
state => state.categories || [] // Assuming categories are loaded here
);

export const selectFeaturedCategories = createSelector(selectAllCategories, categories => categories.filter(category => category.featured));
