import { Component, EventEmitter, Input, Output } from '@angular/core';

@Component({
  selector: 'app-product-sort',
  template: `
    <div class="sort-container">
      <label for="sort-select">Sort By:</label>
      <select 
        id="sort-select" 
        [ngModel]="currentSort" 
        (ngModelChange)="onSortChange($event)"
      >
        <option value="featured">Featured</option>
        <option value="price-low-high">Price: Low to High</option>
        <option value="price-high-low">Price: High to Low</option>
        <option value="name-a-z">Name: A to Z</option>
        <option value="name-z-a">Name: Z to A</option>
        <option value="newest">Newest First</option>
        <option value="rating">Top Rated</option>
        <option value="popular">Most Popular</option>
      </select>
    </div>
  `,
  styles: [`
    :host {
      // Modern Design System - CSS Custom Properties
      --sort-background: rgba(255, 255, 255, 0.95);
      --sort-border: rgba(0, 0, 0, 0.08);
      --sort-border-radius: 12px;
      --sort-shadow: 0 4px 12px rgba(0, 0, 0, 0.08);
      --sort-shadow-hover: 0 8px 20px rgba(0, 0, 0, 0.12);
      --sort-transition: all 0.3s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      --sort-label-color: #6b7280;
      --sort-select-color: #374151;
      --sort-select-background: rgba(255, 255, 255, 0.9);
      --sort-focus-color: #3f51b5;
      --sort-focus-shadow: 0 0 0 3px rgba(63, 81, 181, 0.1);
      --sort-dropdown-icon: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='%23374151' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e");
    }

    .sort-container {
      display: inline-flex;
      align-items: center;
      gap: 0.75rem;
      background: var(--sort-background);
      border: 1px solid var(--sort-border);
      border-radius: var(--sort-border-radius);
      padding: 0.75rem 1rem;
      box-shadow: var(--sort-shadow);
      backdrop-filter: blur(8px);
      -webkit-backdrop-filter: blur(8px);
      transition: var(--sort-transition);

      &:hover {
        box-shadow: var(--sort-shadow-hover);
        transform: translateY(-1px);
      }

      &:focus-within {
        box-shadow: 
          var(--sort-shadow-hover),
          var(--sort-focus-shadow);
        transform: translateY(-1px);
      }
    }

    label {
      margin: 0;
      font-size: 0.875rem;
      font-weight: 500;
      color: var(--sort-label-color);
      white-space: nowrap;
      user-select: none;
      transition: var(--sort-transition);

      &:hover {
        color: var(--sort-select-color);
      }
    }

    select {
      padding: 0.5rem 2.5rem 0.5rem 0.75rem;
      border: 1px solid var(--sort-border);
      border-radius: 8px;
      background: var(--sort-select-background);
      color: var(--sort-select-color);
      font-size: 0.875rem;
      font-weight: 500;
      appearance: none;
      cursor: pointer;
      transition: var(--sort-transition);
      min-width: 180px;

      // Custom dropdown arrow
      background-image: var(--sort-dropdown-icon);
      background-repeat: no-repeat;
      background-position: right 0.75rem center;
      background-size: 1rem;

      // Enhanced backdrop filter
      backdrop-filter: blur(4px);
      -webkit-backdrop-filter: blur(4px);

      &:hover {
        background: rgba(255, 255, 255, 0.95);
        border-color: var(--sort-focus-color);
        transform: scale(1.01);
      }

      &:focus {
        outline: none;
        border-color: var(--sort-focus-color);
        box-shadow: var(--sort-focus-shadow);
        background: rgba(255, 255, 255, 0.98);
      }

      // Style the options
      option {
        padding: 0.5rem;
        background: white;
        color: var(--sort-select-color);
        font-weight: 400;

        &:hover {
          background: rgba(63, 81, 181, 0.1);
        }

        &:checked {
          background: linear-gradient(135deg, #3f51b5, #ff4081);
          color: white;
        }
      }
    }

    // Alternative Material Design select styling
    &.material-style {
      .sort-container {
        background: transparent;
        border: none;
        box-shadow: none;
        padding: 0;

        &:hover {
          transform: none;
          box-shadow: none;
        }
      }

      select {
        background: var(--sort-select-background);
        border: 2px solid var(--sort-border);
        border-radius: 4px;

        &:focus {
          border-color: var(--sort-focus-color);
          border-width: 2px;
        }
      }
    }

    // Compact size variation
    &.compact {
      .sort-container {
        padding: 0.5rem 0.75rem;
        gap: 0.5rem;
      }

      label {
        font-size: 0.75rem;
      }

      select {
        padding: 0.375rem 2rem 0.375rem 0.5rem;
        font-size: 0.75rem;
        min-width: 140px;
        background-size: 0.875rem;
        background-position: right 0.5rem center;
      }
    }

    // Large size variation
    &.large {
      .sort-container {
        padding: 1rem 1.25rem;
        gap: 1rem;
      }

      label {
        font-size: 1rem;
      }

      select {
        padding: 0.75rem 3rem 0.75rem 1rem;
        font-size: 1rem;
        min-width: 220px;
        background-size: 1.25rem;
        background-position: right 1rem center;
      }
    }

    // Dark mode support
    @media (prefers-color-scheme: dark) {
      :host {
        --sort-background: rgba(0, 0, 0, 0.3);
        --sort-border: rgba(255, 255, 255, 0.1);
        --sort-label-color: #d1d5db;
        --sort-select-color: #f9fafb;
        --sort-select-background: rgba(0, 0, 0, 0.2);
        --sort-dropdown-icon: url("data:image/svg+xml;charset=UTF-8,%3csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 24 24' fill='none' stroke='%23f9fafb' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'%3e%3cpolyline points='6 9 12 15 18 9'%3e%3c/polyline%3e%3c/svg%3e");
      }

      select option {
        background: #1f2937;
        color: #f9fafb;

        &:checked {
          background: linear-gradient(135deg, #3f51b5, #ff4081);
          color: white;
        }
      }
    }

    // High contrast support
    @media (prefers-contrast: high) {
      :host {
        --sort-background: #ffffff;
        --sort-border: #000000;
        --sort-label-color: #000000;
        --sort-select-color: #000000;
        --sort-select-background: #ffffff;
        --sort-focus-color: #000000;
      }

      .sort-container {
        border-width: 2px;
      }

      select {
        border-width: 2px;

        &:focus {
          border-width: 3px;
        }
      }
    }

    // Reduced motion support
    @media (prefers-reduced-motion: reduce) {
      :host {
        --sort-transition: none;
      }

      .sort-container:hover {
        transform: none;
      }

      select:hover {
        transform: none;
      }
    }
  `]
})
export class ProductSortComponent {
  @Input() currentSort: string = 'featured';
  @Output() sortChanged = new EventEmitter<string>();

  onSortChange(value: string) {
    this.sortChanged.emit(value);
  }
}
