import { AppState } from '../../../store'; // Assuming AppState is defined
import * as CartActions from '../../../store/actions/cart.actions'; // Import CartActions
import { Observable, of } from 'rxjs'; // Import Observable and of for isInWishlist$
import { WishlistService } from '../../../core/services/wishlist.service'; // Assuming you have a WishlistService
import { map } from 'rxjs/operators';


@Component({
  selector: 'app-product-card',
  templateUrl: './product-card.component.html',
  styleUrls: ['./product-card.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class ProductCardComponent implements OnInit, OnChanges {
  @Input() product!: Product;
  @Input() currencySymbol: string = '$';
  @Input() newThresholdDays: number = 7; // Days to consider a product "new"

  // Configuration for what to show
  @Input() showNewBadge: boolean = true;
  @Input() showFeaturedBadge: boolean = true;
  @Input() showBestsellerBadge: boolean = true;
  @Input() showSoldOutBadge: boolean = true;
  @Input() showImageHoverControls: boolean = true;
  @Input() showImageIndicators: boolean = true;
  @Input() showStockIndicator: boolean = true;
  @Input() showRating: boolean = true;
  @Input() showShortDescription: boolean = false;
  @Input() showKeyFeatures: boolean = false;
  @Input() showShippingInfo: boolean = false;
  @Input() cycleImagesOnHover: boolean = true;


  // Event Emitters - Renamed to avoid conflict with method names
  @Output() productAddedToCart = new EventEmitter<{product: Product, quantity: number}>();
  @Output() productAddedToWishlist = new EventEmitter<Product>();
  @Output() productCompared = new EventEmitter<Product>();
  @Output() productViewed = new EventEmitter<Product>(); // For quick view
  @Output() productBoughtNow = new EventEmitter<{product: Product, quantity: number}>();


  isNew = false;
  isBestSeller = false;
  isLowStock = false;
  discountPercentage: number | null = null;
  discountAmount: number | null = null;

  currentImageIndex = 0;
  private imageChangeInterval: any;

  addingToCart = false;
  addingToWishlistState = false; // Renamed to avoid conflict
  uiShowQuantitySelector = false; // Renamed to avoid conflict
  selectedQuantity: number = 1;

  isInWishlist$: Observable<boolean> = of(false); // Default to false
  isInCompare$: Observable<boolean> = of(false); // Placeholder for compare feature


  constructor(
    private store: Store<AppState>, // Inject Store
    private wishlistService: WishlistService, // Inject WishlistService
    private cdr: ChangeDetectorRef
  ) {}

  ngOnInit(): void {
    this.updateProductFlags();
    this.calculateDiscount();
    if (this.product) {
        this.isInWishlist$ = this.wishlistService.isInWishlist(this.product.id);
        // this.isInCompare$ = this.compareService.isInCompare(this.product.id); // If you have a compare service
    }
  }

  ngOnChanges(changes: SimpleChanges): void {
    if (changes['product'] && this.product) {
      this.updateProductFlags();
      this.calculateDiscount();
      this.selectedQuantity = 1; // Reset quantity when product changes
      this.uiShowQuantitySelector = false;
      this.isInWishlist$ = this.wishlistService.isInWishlist(this.product.id);
      // this.isInCompare$ = this.compareService.isInCompare(this.product.id);
      this.cdr.markForCheck();
    }
  }

  private updateProductFlags(): void {
    if (!this.product) return;

    const now = new Date();
    const productDate = this.product.createdAt ? new Date(this.product.createdAt) : now;
    const diffTime = Math.abs(now.getTime() - productDate.getTime());
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    this.isNew = diffDays <= (this.newThresholdDays || 7);

    this.isBestSeller = !!this.product.featured ||
                       ( (this.product.rating || 0) >= 4.5 && (this.product.reviews?.length || 0) >= 50); // Adjusted review count

    this.isLowStock = (this.product.stockQuantity || 0) > 0 && (this.product.stockQuantity || 0) <= 10;
  }

  private calculateDiscount(): void {
    if (!this.product) {
      this.discountPercentage = null;
      this.discountAmount = null;
      return;
    }
    if (this.product.regularPrice && this.product.regularPrice > this.product.price) {
      this.discountPercentage = Math.round(
        ((this.product.regularPrice - this.product.price) / this.product.regularPrice) * 100
      );
      this.discountAmount = this.product.regularPrice - this.product.price;
    } else {
      this.discountPercentage = null;
      this.discountAmount = null;
    }
  }

  // Mouse enter/leave handlers for image cycling
  onMouseEnter(): void {
    if (this.cycleImagesOnHover) {
      this.startImageCycle();
    }
  }

  onMouseLeave(): void {
    if (this.cycleImagesOnHover) {
      this.stopImageCycle();
    }
  }

  startImageCycle(): void {
    if (this.cycleImagesOnHover && this.product?.images && this.product.images.length > 1) {
      this.stopImageCycle(); // Clear existing interval
      this.imageChangeInterval = setInterval(() => {
        if (this.product.images && this.product.images.length > 1) {
          this.currentImageIndex = (this.currentImageIndex + 1) % this.product.images.length;
          this.cdr.markForCheck(); // Trigger change detection
        }
      }, 2000); // Change image every 2 seconds
    }
  }

  stopImageCycle(): void {
    if (this.imageChangeInterval) {
      clearInterval(this.imageChangeInterval);
      this.imageChangeInterval = null;
    }
  }

  // Image navigation
  prevImage(event: MouseEvent): void {
    event.stopPropagation();
    if (this.product?.images && this.product.images.length > 0) {
      this.currentImageIndex = (this.currentImageIndex - 1 + this.product.images.length) % this.product.images.length;
    }
  }

  nextImage(event: MouseEvent): void {
    event.stopPropagation();
    if (this.product?.images && this.product.images.length > 0) {
      this.currentImageIndex = (this.currentImageIndex + 1) % this.product.images.length;
    }
  }

  setImage(index: number, event: MouseEvent): void {
    event.stopPropagation();
    this.currentImageIndex = index;
  }

  onImageError(event: Event): void {
    const imgElement = event.target as HTMLImageElement;
    imgElement.src = 'assets/images/placeholder.png'; // Fallback image
  }


  // Action Methods
  addToCartHandler(event: MouseEvent): void { // Renamed method
    event.stopPropagation();
    if ((this.product?.stockQuantity || 0) === 0 || this.addingToCart) return;

    this.addingToCart = true;
    // Simulate API call
    setTimeout(() => {
      this.store.dispatch(CartActions.addItem({ item: { productId: this.product.id, quantity: this.selectedQuantity, unitPrice: this.product.price, name: this.product.name, imageUrl: this.product.imageUrl } }));
      this.productAddedToCart.emit({product: this.product, quantity: this.selectedQuantity});
      this.addingToCart = false;
      this.uiShowQuantitySelector = false; // Hide quantity selector after adding
      this.selectedQuantity = 1; // Reset quantity
      this.cdr.markForCheck();
    }, 500);
  }

  addToWishlistHandler(event: MouseEvent): void { // Renamed method
    event.stopPropagation();
    if (this.addingToWishlistState) return;

    this.addingToWishlistState = true;
    this.wishlistService.toggleWishlist(this.product.id).subscribe(() => {
        this.productAddedToWishlist.emit(this.product);
        this.addingToWishlistState = false;
        this.isInWishlist$ = this.wishlistService.isInWishlist(this.product.id); // Re-check status
        this.cdr.markForCheck();
    });
  }

  compareProductHandler(event: MouseEvent): void {
    event.stopPropagation();
    this.productCompared.emit(this.product);
    // Add logic for compare service if available
    // this.isInCompare$ = this.compareService.toggleCompare(this.product.id);
    this.cdr.markForCheck();
  }

  viewProductHandler(event: MouseEvent): void {
    event.stopPropagation();
    this.productViewed.emit(this.product);
    // Logic for opening a quick view modal, for example
  }


  buyNowHandler(event: MouseEvent): void { // Renamed method
    event.stopPropagation();
    if ((this.product?.stockQuantity || 0) === 0) return;

    this.store.dispatch(CartActions.addItem({ item: { productId: this.product.id, quantity: this.selectedQuantity, unitPrice: this.product.price, name: this.product.name, imageUrl: this.product.imageUrl } }));
    this.productBoughtNow.emit({product: this.product, quantity: this.selectedQuantity});
    // Typically, you'd navigate to checkout here or dispatch another action
    this.store.dispatch(CartActions.navigateToCheckout()); // Example action
    this.cdr.markForCheck();
  }

  toggleQuantitySelectorHandler(event: MouseEvent): void { // Renamed method
    event.stopPropagation();
    if ((this.product?.stockQuantity || 0) === 0) return;
    this.uiShowQuantitySelector = !this.uiShowQuantitySelector;
  }

  onQuantityChange(): void {
    if (this.selectedQuantity < 1) {
      this.selectedQuantity = 1;
    }
    if (this.product?.stockQuantity && this.selectedQuantity > this.product.stockQuantity) {
      this.selectedQuantity = this.product.stockQuantity;
    }
  }

  incrementQuantity(event: MouseEvent): void {
    event.stopPropagation();
    if (this.product?.stockQuantity && this.selectedQuantity < this.product.stockQuantity) {
      this.selectedQuantity++;
    }
  }

  decrementQuantity(event: MouseEvent): void {
    event.stopPropagation();
    if (this.selectedQuantity > 1) {
      this.selectedQuantity--;
    }
  }

  get currentDisplayImage(): string {
    if (this.product?.images && this.product.images.length > 0) {
      return this.product.images[this.currentImageIndex]?.url || this.product.imageUrl;
    }
    return this.product?.imageUrl || 'assets/images/placeholder.png';
  }

  get stockStatusClass(): string {
    if ((this.product?.stockQuantity || 0) === 0) return 'out-of-stock';
    if ((this.product?.stockQuantity || 0) <= 10 && (this.product?.stockQuantity || 0) > 0) return 'low-stock'; // Fixed condition
    return 'in-stock';
  }

  get cardClasses(): string[] {
    const classes = ['product-card'];
    if (this.product?.featured) {
      classes.push('product-card--featured');
    }
    if (this.isNew) {
      classes.push('product-card--new');
    }
    if (this.isBestSeller) {
      classes.push('product-card--bestseller');
    }
    if ((this.product?.stockQuantity || 0) === 0) {
      classes.push('product-card--out-of-stock');
    }
    return classes;
  }
}
<mat-card [ngClass]="cardClasses" class="product-card-item" 
     [class.out-of-stock]="(product?.stockQuantity || 0) === 0"
     [class.featured]="product?.featured"
     (mouseenter)="onMouseEnter()" 
     (mouseleave)="onMouseLeave()">

  <div class="product-card-header">
    <a [routerLink]="['/products', product.id]" class="product-image-link">
      <img mat-card-image [src]="currentDisplayImage" [alt]="product.name" 
           class="product-image" loading="lazy"
           (error)="onImageError($event)">
    </a>
    <!-- Badges -->
    <div class="badges-container">
      <div class="badge discount-badge" *ngIf="discountPercentage !== null && discountPercentage > 0">
        -{{ discountPercentage }}%
      </div>
      <div class="badge new-badge" *ngIf="isNew && showNewBadge">NEW</div>
      <div class="badge featured-badge" *ngIf="product?.featured && showFeaturedBadge">FEATURED</div>
      <div class="badge bestseller-badge" *ngIf="isBestSeller && showBestsellerBadge">BESTSELLER</div>
      <div class="badge sold-out-badge" *ngIf="(product?.stockQuantity || 0) === 0 && showSoldOutBadge">SOLD OUT</div>
    </div>

    <!-- Quick Actions -->
    <div class="quick-actions">
      <button mat-icon-button class="action-btn" 
              (click)="addToWishlistHandler($event)" 
              [disabled]="addingToWishlistState"
              matTooltip="Add to Wishlist" aria-label="Add to wishlist">
        <mat-icon>{{ (isInWishlist$ | async) ? 'favorite' : 'favorite_border' }}</mat-icon>
      </button>
      <button mat-icon-button class="action-btn" 
              (click)="viewProductHandler($event)"
              matTooltip="Quick View" aria-label="Quick view product">
        <mat-icon>visibility</mat-icon>
      </button>
      <button mat-icon-button class="action-btn" 
              (click)="compareProductHandler($event)"
              [matTooltip]="(isInCompare$ | async) ? 'Remove from compare' : 'Add to compare'" 
              aria-label="Compare product">
        <mat-icon>{{ (isInCompare$ | async) ? 'check_circle' : 'compare_arrows' }}</mat-icon>
      </button>
    </div>

    <!-- Image hover controls -->
    <div class="image-hover-controls" *ngIf="product?.images && product.images.length > 1 && showImageHoverControls">
      <button mat-icon-button class="hover-control prev" (click)="prevImage($event)" aria-label="Previous image">
        <mat-icon>chevron_left</mat-icon>
      </button>
      <button mat-icon-button class="hover-control next" (click)="nextImage($event)" aria-label="Next image">
        <mat-icon>chevron_right</mat-icon>
      </button>
    </div>

    <!-- Image Indicators -->
    <div class="image-indicators" *ngIf="product?.images && product.images.length > 1 && showImageIndicators">
      <span class="indicator-dot" 
            *ngFor="let image of product.images; let i = index"
            [class.active]="i === currentImageIndex"
            (click)="setImage(i, $event)"></span>
    </div>

    <!-- Stock Indicator -->
    <div class="stock-indicator" *ngIf="showStockIndicator && product?.stockQuantity !== undefined && product.stockQuantity > 0 && product.stockQuantity <= 10">
      <div class="stock-bar">
        <div class="stock-fill" [style.width.%]="((product?.stockQuantity || 0) / 10) * 100"></div>
      </div>
      <span class="stock-text" *ngIf="product?.stockQuantity">Only {{product.stockQuantity}} left</span>
    </div>
  </div>

  <mat-card-content class="product-card-content">
    <div class="product-meta">
      <span class="product-brand" *ngIf="product?.brand">{{product.brand}}</span>
      <span class="product-category" *ngIf="product?.category?.name">{{product.category.name}}</span>
    </div>

    <a [routerLink]="['/products', product.id]" class="product-name-link">
      <h3 class="product-name" matTooltip="{{product.name}}">{{product.name}}</h3>
    </a>

    <div class="product-rating" *ngIf="showRating && product?.rating !== undefined && product.rating > 0">
      <app-star-rating 
        [rating]="product.rating || 0" 
        [reviewCount]="product.reviews?.length || 0"
        [readonly]="true">
      </app-star-rating>
    </div>

    <div class="product-description" *ngIf="showShortDescription">
      <p>{{ product.description | truncate:80 }}</p>
    </div>

    <div class="product-price-container">
      <p class="product-price">
        <span class="current-price">{{ (product.discountPrice || product.price) | currency:currencySymbol }}</span>
        <span class="regular-price" *ngIf="product?.regularPrice && product.regularPrice > product.price">
          {{product.regularPrice | currency:currencySymbol}}
        </span>
        <span class="discount-amount savings" *ngIf="discountAmount !== null && discountAmount > 0"> <!-- Added class savings -->
          Save {{discountAmount | currency:currencySymbol}}
        </span>
      </p>
      <p class="price-per-unit" *ngIf="product?.unit && product?.unitQuantity">
          {{(product.price / product.unitQuantity!) | currency:currencySymbol}}/{{product.unit}}
      </p>
    </div>

    <div class="product-features" *ngIf="showKeyFeatures && product?.keyFeatures && product.keyFeatures.length > 0">
      <span class="feature" *ngFor="let feature of product.keyFeatures.slice(0, 2)">
        <mat-icon class="feature-icon">check_circle_outline</mat-icon> {{feature}}
      </span>
    </div>

    <div class="shipping-info" *ngIf="showShippingInfo">
      <div class="shipping-item" *ngIf="product?.freeShipping">
        <mat-icon>local_shipping</mat-icon> Free Shipping
      </div>
      <div class="shipping-item" *ngIf="product?.fastDelivery">
        <mat-icon>flash_on</mat-icon> Fast Delivery
      </div>
    </div>
  </mat-card-content>

  <mat-card-actions class="product-card-actions" [ngClass]="{'show-quantity': uiShowQuantitySelector}">
    <div class="main-actions">
      <button mat-flat-button color="primary" class="add-to-cart-btn" 
              (click)="addToCartHandler($event)" 
              [disabled]="(product?.stockQuantity || 0) === 0 || addingToCart">
        <mat-icon *ngIf="!addingToCart">add_shopping_cart</mat-icon>
        <mat-progress-spinner *ngIf="addingToCart" mode="indeterminate" diameter="20"></mat-progress-spinner>
        <span *ngIf="!addingToCart">Add to Cart</span>
        <span *ngIf="addingToCart">Adding...</span>
      </button>
    </div>
    <div class="quantity-controls" *ngIf="uiShowQuantitySelector && (product?.stockQuantity || 0) > 0">
       <button mat-icon-button (click)="decrementQuantity($event)" [disabled]="selectedQuantity === 1">
        <mat-icon>remove</mat-icon>
      </button>
      <input type="number" class="quantity-input" 
             [(ngModel)]="selectedQuantity" 
             min="1" [max]="product?.stockQuantity || 1" 
             (change)="onQuantityChange()" (click)="$event.stopPropagation()">
      <button mat-icon-button (click)="incrementQuantity($event)" [disabled]="selectedQuantity >= (product?.stockQuantity || 1)">
        <mat-icon>add</mat-icon>
      </button>
    </div>
     <button mat-stroked-button class="buy-now-btn" 
            (click)="buyNowHandler($event)"
            [disabled]="(product?.stockQuantity || 0) === 0">
      Buy Now
    </button>
  </mat-card-actions>
</mat-card>
