import { Component, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-category-card',
  template: `
    <div class="category-card" [style.background-image]="'url(' + category.imageUrl + ')'" (click)="onClick()">
      <div class="category-overlay">
        <h3>{{ category.name }}</h3>
        <span class="product-count">{{ category.productCount || 0 }} Products</span>
      </div>
    </div>
  `,
  styles: [`
    :host {
      // Modern Design System - CSS Custom Properties
      --category-card-height: clamp(160px, 15vw, 200px);
      --category-card-border-radius: 16px;
      --category-overlay-gradient: linear-gradient(
        135deg,
        transparent 0%,
        rgba(0, 0, 0, 0.3) 40%,
        rgba(0, 0, 0, 0.8) 100%
      );
      --category-hover-scale: 1.05;
      --category-transition: all 0.4s cubic-bezier(0.25, 0.46, 0.45, 0.94);
      --category-shadow: 0 8px 32px rgba(0, 0, 0, 0.12);
      --category-shadow-hover: 0 16px 48px rgba(0, 0, 0, 0.2);
    }

    .category-card {
      position: relative;
      height: var(--category-card-height);
      border-radius: var(--category-card-border-radius);
      background-size: cover;
      background-position: center;
      background-attachment: fixed;
      overflow: hidden;
      cursor: pointer;
      transition: var(--category-transition);
      box-shadow: var(--category-shadow);
      border: 1px solid rgba(255, 255, 255, 0.1);

      // Animated background overlay
      &::before {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background: linear-gradient(
          45deg,
          rgba(255, 64, 129, 0.1) 0%,
          rgba(63, 81, 181, 0.1) 100%
        );
        opacity: 0;
        transition: opacity 0.3s ease;
        z-index: 1;
      }

      &:hover {
        transform: translateY(-8px) scale(var(--category-hover-scale));
        box-shadow: var(--category-shadow-hover);

        &::before {
          opacity: 1;
        }

        .category-overlay {
          background: linear-gradient(
            135deg,
            rgba(255, 64, 129, 0.2) 0%,
            rgba(63, 81, 181, 0.3) 50%,
            rgba(0, 0, 0, 0.9) 100%
          );
          backdrop-filter: blur(8px);

          h3 {
            transform: translateY(-4px);
            text-shadow: 0 4px 16px rgba(0, 0, 0, 0.5);
          }

          .product-count {
            transform: translateY(-2px);
            opacity: 1;
          }
        }
      }
    }

    .category-overlay {
      position: absolute;
      bottom: 0;
      left: 0;
      right: 0;
      padding: clamp(1rem, 3vw, 1.5rem);
      background: var(--category-overlay-gradient);
      color: white;
      text-align: center;
      transition: var(--category-transition);
      z-index: 2;
      border-radius: 0 0 var(--category-card-border-radius) var(--category-card-border-radius);
    }

    h3 {
      margin: 0 0 0.5rem 0;
      font-size: clamp(1.1rem, 2.5vw, 1.3rem);
      font-weight: 700;
      background: linear-gradient(
        135deg,
        #ffffff 0%,
        #f8f9fa 100%
      );
      background-clip: text;
      -webkit-background-clip: text;
      -webkit-text-fill-color: transparent;
      text-shadow: none;
      transition: var(--category-transition);
      position: relative;

      &::after {
        content: '';
        position: absolute;
        bottom: -4px;
        left: 50%;
        transform: translateX(-50%);
        width: 0;
        height: 2px;
        background: linear-gradient(90deg, #ff4081, #3f51b5);
        transition: width 0.3s ease;
      }
    }

    .category-card:hover h3::after {
      width: 60%;
    }

    .product-count {
      font-size: clamp(0.8rem, 2vw, 0.95rem);
      opacity: 0.9;
      font-weight: 500;
      color: rgba(255, 255, 255, 0.95);
      transition: var(--category-transition);
      text-shadow: 0 2px 8px rgba(0, 0, 0, 0.3);
    }

    // Responsive adjustments
    @media (max-width: 768px) {
      :host {
        --category-card-height: clamp(140px, 12vw, 180px);
        --category-card-border-radius: 12px;
        --category-hover-scale: 1.02;
      }

      .category-card {
        background-attachment: scroll; // Better performance on mobile

        &:hover {
          transform: translateY(-4px) scale(var(--category-hover-scale));
        }
      }

      .category-overlay {
        padding: clamp(0.75rem, 2vw, 1rem);
      }
    }

    // Accessibility improvements
    @media (prefers-reduced-motion: reduce) {
      :host {
        --category-transition: none;
      }

      .category-card {
        &:hover {
          transform: none;
        }
      }
    }

    @media (prefers-contrast: high) {
      .category-overlay {
        background: linear-gradient(
          135deg,
          rgba(0, 0, 0, 0.8) 0%,
          rgba(0, 0, 0, 0.95) 100%
        );
      }

      h3 {
        -webkit-text-fill-color: white;
        color: white;
      }
    }
  `]
})
export class CategoryCardComponent {
  @Input() category: any;
  @Output() categorySelected = new EventEmitter<any>();

  onClick() {
    this.categorySelected.emit(this.category);
  }
}
